// ============================================
// PREPARE ANALYTICS CALCULATION
// ============================================

const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
yesterday.setHours(0, 0, 0, 0);

const today = new Date();
today.setHours(0, 0, 0, 0);

console.log(`Calculating analytics for date: ${yesterday.toISOString().split('T')[0]}`);

return {
  json: {
    analytics_date: yesterday.toISOString().split('T')[0],
    start_timestamp: yesterday.toISOString(),
    end_timestamp: today.toISOString()
  }
};

// ============================================
// CHECK IF CAMPAIGNS EXIST
// ============================================

const campaigns = $input.first().json;

if (!campaigns || campaigns.length === 0) {
  console.log('No active campaigns to analyze');
  return {
    json: {
      campaigns_analyzed: 0,
      message: 'No active campaigns'
    }
  };
}

console.log(`Found ${campaigns.length} active campaigns`);

return campaigns.map(c => ({ json: c }));

// ============================================
// PREPARE ANALYTICS INSERT DATA
// ============================================

const campaignData = $('Check Campaigns').item.json;
const metricsResult = $input.first().json;
const analyticsDate = $('Prepare Date Range').first().json.analytics_date;

if (!metricsResult || metricsResult.length === 0) {
  // No data for this campaign yesterday
  return {
    json: {
      campaign_id: campaignData.campaign_id,
      organization_id: campaignData.organization_id,
      analytics_date: analyticsDate,
      leads_enrolled_today: 0,
      emails_sent_today: 0,
      emails_scheduled_today: 0,
      unique_opens_today: 0,
      unique_clicks_today: 0,
      unsubscribes_today: 0,
      bounces_today: 0,
      open_rate: 0,
      click_rate: 0,
      click_to_open_rate: 0,
      unsubscribe_rate: 0,
      variant_a_opens: 0,
      variant_a_clicks: 0,
      variant_b_opens: 0,
      variant_b_clicks: 0
    }
  };
}

const metrics = metricsResult[0];

return {
  json: {
    ...metrics,
    analytics_date: analyticsDate
  }
};

// ============================================
// DETECT PERFORMANCE ANOMALIES
// ============================================

const metrics = $('Prepare Insert').first().json;
const campaignData = $('Check Campaigns').item.json;

const alerts = [];

// Alert if open rate drops below 20%
if (metrics.emails_sent_today > 10 && metrics.open_rate < 20) {
  alerts.push({
    severity: 'warning',
    metric: 'open_rate',
    value: metrics.open_rate,
    message: `Open rate (${metrics.open_rate}%) is below 20% threshold`
  });
}

// Alert if click rate drops below 2%
if (metrics.emails_sent_today > 10 && metrics.click_rate < 2) {
  alerts.push({
    severity: 'warning',
    metric: 'click_rate',
    value: metrics.click_rate,
    message: `Click rate (${metrics.click_rate}%) is below 2% threshold`
  });
}

// Alert if unsubscribe rate exceeds 1%
if (metrics.emails_sent_today > 10 && metrics.unsubscribe_rate > 1) {
  alerts.push({
    severity: 'critical',
    metric: 'unsubscribe_rate',
    value: metrics.unsubscribe_rate,
    message: `Unsubscribe rate (${metrics.unsubscribe_rate}%) exceeds 1% threshold`
  });
}

// Alert if bounce rate exceeds 5%
const bounceRate = metrics.emails_sent_today > 0 
  ? (metrics.bounces_today / metrics.emails_sent_today) * 100 
  : 0;
if (metrics.emails_sent_today > 10 && bounceRate > 5) {
  alerts.push({
    severity: 'warning',
    metric: 'bounce_rate',
    value: bounceRate,
    message: `Bounce rate (${bounceRate.toFixed(2)}%) exceeds 5% threshold`
  });
}

if (alerts.length > 0) {
  console.log(`âš ï¸  Alerts for campaign ${campaignData.campaign_name}:`, alerts);
}

return {
  json: {
    campaign_id: campaignData.campaign_id,
    campaign_name: campaignData.campaign_name,
    metrics: metrics,
    alerts: alerts,
    has_alerts: alerts.length > 0
  }
};

// ============================================
// SEND ALERT EMAIL (Optional)
// ============================================

const data = $json;

console.log(`Sending performance alert for campaign: ${data.campaign_name}`);
console.log('Alerts:', data.alerts);

// In production, this would send an email via SendGrid
// or post to a Slack channel

return {
  json: {
    alert_sent: true,
    campaign_name: data.campaign_name,
    alert_count: data.alerts.length
  }
};

// ============================================
// AGGREGATE ANALYTICS SUMMARY
// ============================================

const allItems = $input.all();
const campaignsAnalyzed = allItems.length;
const campaignsWithAlerts = allItems.filter(item => item.json.has_alerts).length;

const totalMetrics = allItems.reduce((acc, item) => {
  const metrics = item.json.metrics || {};
  return {
    total_emails_sent: acc.total_emails_sent + (metrics.emails_sent_today || 0),
    total_opens: acc.total_opens + (metrics.unique_opens_today || 0),
    total_clicks: acc.total_clicks + (metrics.unique_clicks_today || 0),
    total_unsubscribes: acc.total_unsubscribes + (metrics.unsubscribes_today || 0)
  };
}, {
  total_emails_sent: 0,
  total_opens: 0,
  total_clicks: 0,
  total_unsubscribes: 0
});

const overallOpenRate = totalMetrics.total_emails_sent > 0
  ? ((totalMetrics.total_opens / totalMetrics.total_emails_sent) * 100).toFixed(2)
  : 0;

const overallClickRate = totalMetrics.total_emails_sent > 0
  ? ((totalMetrics.total_clicks / totalMetrics.total_emails_sent) * 100).toFixed(2)
  : 0;

