// ===============================================
// FACEBOOK POST SYNC
// ===============================================
const account = $input.first().json;

// Facebook Graph API call
// URL: https://graph.facebook.com/v18.0/{page-id}/posts
// Params: fields=id,message,created_time,permalink_url,attachments,insights.metric(post_impressions,post_engaged_users,post_reactions_like_total,post_reactions_love_total,post_comments,post_shares)

const apiUrl = `https://graph.facebook.com/v18.0/${account.platform_account_id}/posts`;
const fields = [
  'id',
  'message',
  'created_time',
  'permalink_url',
  'attachments',
  'full_picture',
  'type',
  'insights.metric(post_impressions,post_engaged_users,post_reactions_like_total,post_comments,post_shares)'
].join(',');

// In production, this would make actual API call:
// const response = await fetch(`${apiUrl}?fields=${fields}&access_token=${accessToken}`);

// Mock response for structure
const mockPosts = [
  {
    id: `${account.platform_account_id}_12345`,
    message: 'Check out this stunning luxury property in downtown! ðŸ âœ¨ #RealEstate #LuxuryHomes #PropertyListing',
    created_time: new Date(Date.now() - 6 * 60 * 60 * 1000).toISOString(),
    permalink_url: `https://facebook.com/${account.platform_account_id}/posts/12345`,
    type: 'photo',
    full_picture: 'https://example.com/property-image.jpg',
    insights: {
      data: [
        {name: 'post_impressions', values: [{value: 2500}]},
        {name: 'post_engaged_users', values: [{value: 185}]},
        {name: 'post_reactions_like_total', values: [{value: 142}]},
        {name: 'post_comments', values: [{value: 23}]},
        {name: 'post_shares', values: [{value: 12}]}
      ]
    },
    attachments: {
      data: [{media: {image: {src: 'https://example.com/property-image.jpg'}}}]
    }
  },
  {
    id: `${account.platform_account_id}_12346`,
    message: 'Home buying tips for first-time buyers ðŸ“ Link in bio! #HomeBuying #RealEstateTips #FirstTimeHomeBuyer',
    created_time: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
    permalink_url: `https://facebook.com/${account.platform_account_id}/posts/12346`,
    type: 'link',
    insights: {
      data: [
        {name: 'post_impressions', values: [{value: 1800}]},
        {name: 'post_engaged_users', values: [{value: 95}]},
        {name: 'post_reactions_like_total', values: [{value: 67}]},
        {name: 'post_comments', values: [{value: 15}]},
        {name: 'post_shares', values: [{value: 8}]}
      ]
    }
  }
];

// Transform to our schema
const transformedPosts = mockPosts.map(post => {
  // Extract insights
  const insights = {};
  if (post.insights && post.insights.data) {
    post.insights.data.forEach(metric => {
      insights[metric.name] = metric.values[0].value;
    });
  }

  // Extract hashtags
  const hashtagRegex = /#(\w+)/g;
  const hashtags = [];
  let match;
  while ((match = hashtagRegex.exec(post.message || '')) !== null) {
    hashtags.push(match[1]);
  }

  // Determine content type
  let contentType = 'text';
  let mediaUrls = [];
  if (post.type === 'photo') {
    contentType = 'image';
    if (post.full_picture) mediaUrls.push(post.full_picture);
  } else if (post.type === 'video') {
    contentType = 'video';
  } else if (post.type === 'link') {
    contentType = 'text';
  }

  if (post.attachments && post.attachments.data) {
    post.attachments.data.forEach(att => {
      if (att.media && att.media.image) {
        mediaUrls.push(att.media.image.src);
        if (contentType === 'text') contentType = 'image';
      }
    });
  }

  return {
    organization_id: account.organization_id,
    account_id: account.id,
    platform: 'facebook',
    platform_post_id: post.id,
    post_url: post.permalink_url,
    content_text: post.message || '',
    content_type: contentType,
    media_urls: JSON.stringify(mediaUrls),
    media_count: mediaUrls.length,
    published_at: post.created_time,
    is_published: true,
    hashtags: JSON.stringify(hashtags),
    likes_count: insights.post_reactions_like_total || 0,
    comments_count: insights.post_comments || 0,
    shares_count: insights.post_shares || 0,
    reach: insights.post_engaged_users || 0,
    impressions: insights.post_impressions || 0,
    last_metrics_update: new Date().toISOString()
  };
});

return transformedPosts.map(post => ({json: post}));

