// INSTAGRAM POST SYNC
// ===============================================
const account = $input.first().json;

// Instagram Graph API
// URL: https://graph.facebook.com/v18.0/{ig-user-id}/media
// Fields: id,caption,media_type,media_url,permalink,timestamp,like_count,comments_count,insights.metric(impressions,reach,engagement)

const mockPosts = [
  {
    id: 'ig_post_1',
    caption: 'Breathtaking sunset views from this penthouse! ðŸŒ… #LuxuryLiving #Penthouse #RealEstateGoals #DreamHome',
    media_type: 'IMAGE',
    media_url: 'https://example.com/ig-image.jpg',
    permalink: `https://instagram.com/p/ABC123`,
