    insights: {
      data: [
        {name: 'impressions', values: [{value: 5200}]},
        {name: 'reach', values: [{value: 3900}]},
        {name: 'engagement', values: [{value: 715}]},
        {name: 'video_views', values: [{value: 2850}]}
      ]
    }
  }
];

const transformedPosts = mockPosts.map(post => {
  const insights = {};
  if (post.insights && post.insights.data) {
    post.insights.data.forEach(metric => {
      insights[metric.name] = metric.values[0].value;
    });
  }

  const hashtagRegex = /#(\w+)/g;
  const hashtags = [];
  let match;
  while ((match = hashtagRegex.exec(post.caption || '')) !== null) {
    hashtags.push(match[1]);
  }

  let contentType = 'text';
  if (post.media_type === 'IMAGE') contentType = 'image';
  else if (post.media_type === 'VIDEO') contentType = 'video';
  else if (post.media_type === 'CAROUSEL_ALBUM') contentType = 'carousel';

  return {
    organization_id: account.organization_id,
    account_id: account.id,
    platform: 'instagram',
    platform_post_id: post.id,
    post_url: post.permalink,
    content_text: post.caption || '',
    content_type: contentType,
    media_urls: post.media_url ? JSON.stringify([post.media_url]) : '[]',
    media_count: post.media_url ? 1 : 0,
    published_at: post.timestamp,
    is_published: true,
    hashtags: JSON.stringify(hashtags),
    likes_count: post.like_count || 0,
    comments_count: post.comments_count || 0,
    shares_count: 0, // Instagram doesn't track shares
    saves_count: insights.saved || 0,
    views_count: post.video_views || 0,
    reach: insights.reach || 0,
    impressions: insights.impressions || 0,
    last_metrics_update: new Date().toISOString()
  };
});

return transformedPosts.map(post => ({json: post}));

// ===============================================
