const account = $input.first().json;

// Twitter API v2
// URL: https://api.twitter.com/2/users/{id}/tweets
// Fields: id,text,created_at,public_metrics,entities

const mockTweets = [
  {
    id: 'tweet_123',
    text: 'Just listed! Stunning 4BR home with pool and mountain views. Schedule your showing today! ðŸ¡ #RealEstate #HomesForSale #DreamHome',
