// ===============================================
// LINKEDIN POST SYNC
// ===============================================
const account = $input.first().json;

// LinkedIn API
// URL: https://api.linkedin.com/v2/ugcPosts

const mockPosts = [
  {
    id: 'urn:li:share:7123456789',
    text: 'Excited to announce our latest commercial property development in the heart of downtown. State-of-the-art office spaces now available. Learn more in the comments. #CommercialRealEstate #OfficeSpace #BusinessGrowth',
    created: new Date(Date.now() - 15 * 60 * 60 * 1000).toISOString(),
    numLikes: 234,
    numComments: 45,
    numShares: 28,
    numViews: 5600
  },
  {
    id: 'urn:li:share:7123456790',
    text: 'Proud to be recognized as a top real estate agency in the region! Thank you to our amazing clients and team. #RealEstateExcellence #TeamSuccess',
    created: new Date(Date.now() - 40 * 60 * 60 * 1000).toISOString(),
    numLikes: 412,
    numComments: 67,
    numShares: 45,
    numViews: 8200
  }
];

const transformedPosts = mockPosts.map(post => {
  const hashtagRegex = /#(\w+)/g;
  const hashtags = [];
  let match;
  while ((match = hashtagRegex.exec(post.text || '')) !== null) {
    hashtags.push(match[1]);
  }

  return {
    organization_id: account.organization_id,
    account_id: account.id,
    platform: 'linkedin',
    platform_post_id: post.id,
    post_url: `https://linkedin.com/feed/update/${post.id}`,
    content_text: post.text,
    content_type: 'text',
    media_urls: '[]',
    media_count: 0,
    published_at: post.created,
    is_published: true,
    hashtags: JSON.stringify(hashtags),
    likes_count: post.numLikes || 0,
    comments_count: post.numComments || 0,
    shares_count: post.numShares || 0,
    views_count: post.numViews || 0,
    last_metrics_update: new Date().toISOString()
  };
});

return transformedPosts.map(post => ({json: post}));

// Generic fallback for other platforms
const account = $input.first().json;

return [{
  json: {
    account_id: account.id,
    platform: account.platform,
    status: 'skipped',
    message: `Platform ${account.platform} not yet implemented`
  }
}];

// Update hashtag performance
const posts = $input.all();
const hashtagStats = {};

posts.forEach(item => {
  const post = item.json;
  if (!post.hashtags) return;

  let hashtags = [];
  try {
    hashtags = JSON.parse(post.hashtags);
  } catch (e) {
    return;
  }

  hashtags.forEach(tag => {
    const key = `${post.organization_id}:${post.platform}:${tag}`;
    if (!hashtagStats[key]) {
      hashtagStats[key] = {
        organization_id: post.organization_id,
        platform: post.platform,
        hashtag: tag,
        times_used: 0,
        total_likes: 0,
        total_comments: 0,
        total_shares: 0,
        total_impressions: 0,
        engagement_rates: []
      };
    }

    hashtagStats[key].times_used++;
    hashtagStats[key].total_likes += post.likes_count || 0;
    hashtagStats[key].total_comments += post.comments_count || 0;
    hashtagStats[key].total_shares += post.shares_count || 0;
    hashtagStats[key].total_impressions += post.impressions || 0;
    if (post.engagement_rate) {
      hashtagStats[key].engagement_rates.push(post.engagement_rate);
    }
  });
});

// Calculate averages and prepare for upsert
const hashtagRecords = Object.values(hashtagStats).map(stat => {
  const avgEngagement = stat.engagement_rates.length > 0
    ? stat.engagement_rates.reduce((sum, r) => sum + r, 0) / stat.engagement_rates.length
    : 0;

  return {
    organization_id: stat.organization_id,
    platform: stat.platform,
    hashtag: stat.hashtag,
    times_used: stat.times_used,
    avg_engagement_rate: parseFloat(avgEngagement.toFixed(2)),
    total_impressions: stat.total_impressions,
    total_likes: stat.total_likes,
    total_comments: stat.total_comments,
    total_shares: stat.total_shares,
    last_used_at: new Date().toISOString(),
    updated_at: new Date().toISOString()
  };
});

return hashtagRecords.map(record => ({json: record}));

// Summary of sync
const items = $input.all();

const summary = {
  total_posts_synced: items.length,
  platforms: {},
  sync_completed_at: new Date().toISOString()
};

items.forEach(item => {
  const platform = item.json.platform;
  if (!summary.platforms[platform]) {
    summary.platforms[platform] = 0;
  }
  summary.platforms[platform]++;
});

return [{json: summary}];

